   "metadata": {},
   "outputs": [],
   "source": [
    "filename = \"Bloch-V=10.0_k=3.142_num-basis=61_num-q=100.hdf5\"\n",
    "# filename = \"Bloch-V=4.0_k=3.142_num-basis=21_num-q=100.hdf5\"\n",
